// Version of Solidity compiler this program was written for
pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

contract Transcript {
	// Create a state variable for the contract
	string public name;
	address payable owner;

	uint public transcriptCount = 0;
	mapping(string => Result) results;

	uint totalQP;

	constructor() public {
		name = "HCTE II Transcript";
		owner = msg.sender;
	}

	// Product model; each newly placed product will follow this pattern
	struct Result {
		uint id;
		address caller;
		string matricNo;
		string[] examName;
		uint[] examUnit;
		string[] examGrade;
	}

	event From (
		address indexed from, uint amount
	);


	function createTranscript(string memory _matricNo, string[] memory _examName, uint[] memory _examUnit, string[] memory _examGrade) public payable {
		// Require a valid name
		require(bytes(_matricNo).length > 0);
		transcriptCount++;
		results[_matricNo] = Result(transcriptCount, msg.sender,  _matricNo, _examName, _examUnit, _examGrade);
		msg.sender.transfer(5 ether);
	}

	function () external payable {
		emit From(msg.sender, msg.value);
	}
}
